import React, { useState } from 'react';
import axios from 'axios';

const ResumeForm = () => {
  const [formData, setFormData] = useState({
    name: '', email: '', phone: '', summary: '',
    education: '', experience: '', projects: '', skills: ''
  });

  const [aiSuggestions, setAiSuggestions] = useState('');

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/resume/suggest', formData);
      setAiSuggestions(res.data.suggestions);
    } catch (err) {
      console.error(err);
      alert('Failed to get suggestions');
    }
  };

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Smart Resume Builder</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        {['name', 'email', 'phone', 'summary', 'education', 'experience', 'projects', 'skills'].map((field) => (
          <div key={field}>
            <label className="block capitalize font-semibold">{field}</label>
            <textarea
              name={field}
              value={formData[field]}
              onChange={handleChange}
              className="w-full p-2 border rounded"
              rows={field === 'summary' ? 2 : 3}
              required
            />
          </div>
        ))}
        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">
          Get AI Suggestions
        </button>
      </form>

      {aiSuggestions && (
        <div className="mt-6 p-4 border rounded bg-gray-50">
          <h3 className="text-lg font-semibold mb-2">AI Suggestions</h3>
          <pre className="whitespace-pre-wrap">{aiSuggestions}</pre>
        </div>
      )}
    </div>
  );
};

export default ResumeForm;
