import React, { useState } from 'react';
import axios from 'axios';

function Form() {
  const [formData, setFormData] = useState({
    name: '',
    education: '',
    skills: '',
    experience: '',
  });

  const [aiSuggestions, setAiSuggestions] = useState('');

  const handleChange = (e) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/resume/suggest', formData);
      setAiSuggestions(res.data.suggestions || 'No suggestions returned.');
    } catch (err) {
      setAiSuggestions('Error getting suggestions from AI.');
    }
  };

  return (
    <div className="max-w-xl mx-auto mt-10 p-6 bg-white rounded shadow">
      <h2 className="text-2xl font-bold mb-4 text-center">ðŸ§  Smart Resume Builder</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        {['name', 'education', 'skills', 'experience'].map(field => (
          <div key={field}>
            <label className="block text-gray-700 capitalize">{field}</label>
            <textarea
              name={field}
              value={formData[field]}
              onChange={handleChange}
              className="w-full p-2 border rounded"
              rows={field === 'skills' || field === 'experience' ? 3 : 1}
              required
            />
          </div>
        ))}

        <button type="submit" className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700">
          Get AI Suggestions
        </button>
      </form>

      {aiSuggestions && (
        <div className="mt-6 p-4 bg-gray-100 rounded">
          <h3 className="text-lg font-semibold mb-2">âœ¨ AI Suggestions</h3>
          <p>{aiSuggestions}</p>
        </div>
      )}
    </div>
  );
}

export default Form;
