import React, { useState } from "react";
import axios from "axios";

const Form = () => {
  const [formData, setFormData] = useState({
    name: "",
    education: "",
    skills: "",
    experience: "",
  });

  const [suggestion, setSuggestion] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:5000/api/resume", formData);
      setSuggestion(res.data.suggestion); // Assumes API returns { suggestion: "..." }
    } catch (err) {
      console.error(err);
      setSuggestion("‚ùå Error fetching suggestions");
    }
  };

  return (
    <div>
      <h1>üöÄ Smart Resume Builder</h1>
      <form onSubmit={handleSubmit}>
        <label>Name<br />
          <input name="name" value={formData.name} onChange={handleChange} />
        </label><br />

        <label>Education<br />
          <textarea name="education" value={formData.education} onChange={handleChange} />
        </label><br />

        <label>Skills<br />
          <textarea name="skills" value={formData.skills} onChange={handleChange} />
        </label><br />

        <label>Experience<br />
          <textarea name="experience" value={formData.experience} onChange={handleChange} />
        </label><br />

        <button type="submit">Get AI Suggestions</button>
      </form>

      {suggestion && (
        <div style={{ marginTop: "20px", padding: "10px", border: "1px solid black" }}>
          <h3>AI Suggestion:</h3>
          <p>{suggestion}</p>
        </div>
      )}
    </div>
  );
};

export default Form;
